<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet Abattoir------------------------------------------------------*/class Abattoir {		protected	$id,				$nom,				$genlot,				$numagr,				$adresse,				$cp,				$ville,				$pays,				$telephone,				$mobile,				$email,				$actif,				$supprime,				$date_add,				$date_maj;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNom() {		return $this->nom;	}	public function getGenlot() {		return $this->genlot;	}	public function getNumagr() {		return $this->numagr;	}	public function getAdresse() {		return $this->adresse;	}	public function getCp() {		return $this->cp;	}	public function getVille() {		return $this->ville;	}	public function getPays() {		return $this->pays;	}	public function getTelephone() {		return $this->telephone;	}	public function getMobile() {		return $this->mobile;	}	public function getEmail() {		return $this->email;	}		public function getActif() {		return $this->actif;	}	public function getSupprime() {		return $this->supprime;	}	public function getDate_maj() {		return $this->date_maj;	}	public function getDate_add() {		return $this->date_add;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setGenlot($valeur) {		$this->genlot = (string)$valeur;		Outils::setAttributs('genlot',$this);	}	public function setNumagr($valeur) {		$this->numagr = (string)$valeur;		Outils::setAttributs('numagr',$this);	}	public function setAdresse($valeur) {		$this->adresse = (string)$valeur;		Outils::setAttributs('adresse',$this);	}	public function setCp($valeur) {		$this->cp = (string)$valeur;		Outils::setAttributs('cp',$this);	}	public function setVille($valeur) {		$this->ville = (string)$valeur;		Outils::setAttributs('ville',$this);	}	public function setPays($valeur) {		$this->pays = (string)$valeur;		Outils::setAttributs('pays',$this);	}	public function setTelephone($valeur) {		$this->telephone = (string)$valeur;		Outils::setAttributs('telephone',$this);	}	public function setMobile($valeur) {		$this->mobile = (string)$valeur;		Outils::setAttributs('mobile',$this);	}	public function setEmail($valeur) {		$this->email = (string)$valeur;		Outils::setAttributs('email',$this);	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	public function setDate_add($valeur) {		$this->date_add = (string)$valeur;		Outils::setAttributs('date_add',$this);	}	public function setDate_maj($valeur) {		$this->date_maj = (string)$valeur;		Outils::setAttributs('date_maj',$this);	}	/*##### MTHODES PROPRES A LA CLASSE #####*/	public function isActif() {		return (int)$this->actif == 1 && (int)$this->supprime == 0;	}	public function isSupprime() {		return (int)$this->supprime == 1;	}} // FIN classe