<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet Communication------------------------------------------------------*/class Communication {		protected	$id,				$fichier,				$chemin,				$id_vue,				$nom,				$nom_vue,				$date,				$actif,				$supprime;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}	public function getFichier() {		return $this->fichier;	}	public function getChemin() {		return $this->chemin;	}	public function getId_vue() {		return $this->id_vue;	}	public function getNom() {		return $this->nom;	}	public function getNom_vue() {		return $this->nom_vue;	}	public function getDate() {		return $this->date;	}	public function getActif() {		return $this->actif;	}	public function getSupprime() {		return $this->supprime;	}	/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setFichier($valeur) {		$this->fichier = (string)$valeur;		Outils::setAttributs('fichier',$this);	}	public function setChemin($valeur) {		$this->chemin = (string)$valeur;		Outils::setAttributs('chemin',$this);	}	public function setId_vue($valeur) {		$this->id_vue = (int)$valeur;		Outils::setAttributs('id_vue',$this);	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setNom_vue($valeur) {		$this->nom_vue = (string)$valeur;	}	public function setDate($valeur) {		$this->date = (string)$valeur;		Outils::setAttributs('date',$this);	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	/*##### METHODES DE CLASSE #####*/	public function isActif() {		return $this->actif == 1;	}	public function isSupprime() {		return $this->supprime == 1;	}	public function getCheminFichier() {		$chemin = $this->chemin;		if (substr($chemin,-2) == '//') {			$source = substr($chemin,0,-1);		} else if (substr($chemin,-1) != '/') {			$chemin.= '/';		}		return $chemin.$this->fichier;	}} // FIN classe