<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 Cédric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Configuration générale (BDD)------------------------------------------------------*/class Config {		protected	$id,				$clef,				$valeur,				$description,				$date_maj;		public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getClef() {		return $this->clef;	}	public function getValeur() {		return $this->valeur;	}	public function getDescription() {		return $this->description;	}	public function getDate_maj() {		return $this->date_maj;	}				/*##### SETTERS #####*/	public function setId($valeur)	{		$this->id = (int)$valeur;	}	public function setClef($valeur) {		$this->clef = (string)$valeur;		Outils::setAttributs('clef',$this);	}	public function setValeur($valeur) {		$this->valeur = (string)$valeur;		Outils::setAttributs('valeur',$this);	}	public function setDescription($valeur) {		$this->description = (string)$valeur;		Outils::setAttributs('description',$this);	}	public function setDate_maj($valeur) {		$this->date_maj = (string)$valeur;		Outils::setAttributs('date_maj',$this);	}} // FIN classe