<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet ConsommablesHisto------------------------------------------------------*/class ConsommablesHisto {		protected	$id,				$id_consommable,				$stock,				$ecart,				$date,				$user_id,				$id_vue,				$nom_vue,				$user;		// Objet User	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}	public function getId_consommable() {		return $this->id_consommable;	}	public function getStock() {		return $this->stock;	}	public function getEcart() {		return $this->ecart;	}	public function getDate() {		return $this->date;	}	public function getUser_id() {		return $this->user_id;	}	public function getUser() {		return $this->user;	}	public function getId_vue() {		return $this->id_vue;	}	public function getNom_vue() {		return $this->nom_vue;	}	/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setId_consommable($valeur) {		$this->id_consommable = (int)$valeur;		Outils::setAttributs('id_consommable',$this);	}	public function setStock($valeur) {		$this->stock = (int)$valeur;		Outils::setAttributs('stock',$this);	}	public function setDate($valeur) {		$this->date = (string)$valeur;		Outils::setAttributs('date',$this);	}	public function setUser_id($valeur) {		$this->user_id = (int)$valeur;		Outils::setAttributs('user_id',$this);	}	public function setId_vue($valeur) {		$this->id_vue = (int)$valeur;		Outils::setAttributs('id_vue',$this);	}	public function setNom_vue($valeur) {		$this->nom_vue = (string)$valeur;	}	public function setEcart($valeur) {		$this->ecart = (int)$valeur;	}	public function setUser($objet) {		$this->user = $objet;	}} // FIN classe