<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet ConsommablesTypes------------------------------------------------------*/class ConsommablesTypes {		protected	$id,				$nom,				$actif,				$nbFamilles,				$nbConsommables,				$stock_actuel,				$supprime;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}	public function getNom() {		return $this->nom;	}	public function getActif() {		return $this->actif;	}	public function isActif() {		return $this->actif == 1;	}	public function getSupprime() {		return $this->supprime;	}	public function isSupprime() {		return $this->supprime == 1;	}	public function getNbFamilles() {		return $this->nbFamilles;	}	public function getNbConsommables() {		return $this->nbConsommables;	}	public function getStock_actuel() {		return $this->stock_actuel;	}	/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	public function setNbFamilles($valeur) {		$this->nbFamilles = (int)$valeur;	}	public function setNbConsommables($valeur) {		$this->nbConsommables = (int)$valeur;	}	public function setStock_actuel($valeur) {		$this->stock_actuel = (int)$valeur;	}} // FIN classe