<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet DetailsFroidProduit - Pour validations------------------------------------------------------*/class DetailsFroidProduit {	protected		$id_froid,		$id_lot_pdt,		$nb_colis,		$poids,		$produit;		public       $attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId_froid() {		return $this->id_froid;	}	public function getId_lot_pdt() {		return $this->id_lot_pdt;	}	public function getNb_colis() {		return $this->nb_colis;	}	public function getPoids() {		return $this->poids;	}	public function getProduit() {		return $this->produit;	}	/* ----------------- SETTERS ----------------- */	public function setId_froid($valeur) {		$this->id_froid = (int)$valeur;		Outils::setAttributs('id_froid',$this);	}	public function setId_lot_pdt($valeur) {		$this->id_lot_pdt = (int)$valeur;		Outils::setAttributs('id_lot_pdt',$this);	}	public function setNb_colis($valeur) {		$this->nb_colis = (int)$valeur;	}	public function setPoids($valeur) {		$this->poids = (float)$valeur;;	}	public function setProduit($valeur) {		$this->produit = $valeur;	}} // FIN classe