<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet Document------------------------------------------------------*/class Document {		protected	$id,				$lot_id,				$type_lot,				$numlot,				$filename,				$type_id,				$type_nom,				$nom,				$date,				$supprime;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}	public function getLot_id() {		return $this->lot_id;	}	public function getNumlot() {		return $this->numlot;	}	public function getFilename() {		return $this->filename;	}	public function getType_id() {		return $this->type_id;	}	public function getType_nom() {		return $this->type_nom;	}	public function getDate() {		return $this->date;	}		public function getNom() {		return $this->nom;	}	public function getSupprime() {		return $this->supprime;	}	public function getType_lot() {		return $this->type_lot;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setLot_id($valeur) {		$this->lot_id = (int)$valeur;		Outils::setAttributs('lot_id',$this);	}	public function setFilename($valeur) {		$this->filename = (string)$valeur;		Outils::setAttributs('filename',$this);	}	public function setType_id($valeur) {		$this->type_id = (int)$valeur;		Outils::setAttributs('type_id',$this);	}	public function setDate($valeur) {		$this->date = (string)$valeur;		Outils::setAttributs('date',$this);	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setType_nom($valeur) {		$this->type_nom = (string)$valeur;	}	public function setNumlot($valeur) {		$this->numlot = (string)$valeur;	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	public function setType_lot($valeur) {		$this->type_lot = (int)$valeur;		Outils::setAttributs('type_lot',$this);	}} // FIN classe