<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet DocumentsType------------------------------------------------------*/class DocumentsType {		protected	$id,				$nom,				$locked,				$actif,				$api;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNom() {		return $this->nom;	}	public function getActif() {		return $this->actif;	}	public function getLocked() {		return $this->locked;	}	public function getApi() {		return $this->api;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	public function setLocked($valeur) {		$this->locked = (int)$valeur;	}	public function setApi($valeur) {		$this->api = (int)$valeur;	}	/*##### METHODES PROPREES A LA CLASSE #####*/	public function isLocked() {		return (int)$this->locked == 1;	}} // FIN classe