<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 Cédric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet EmballageDefectueux------------------------------------------------------*/class EmballageDefectueux {	protected	$id,				$id_emballage,				$id_lot,			// ID Lot (viande)				$num_lot,			// Numéro de Lot (viande)				$id_froid,			// ID froid				$code_froid,		// Code traitement				$date,				$qte;	public		$attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/*##### GETTERS #####*/	public function getId() {		return $this->id;	}	public function getId_emballage() {		return $this->id_emballage;	}	public function getDate() {		return $this->date;	}	public function getQte() {		return $this->qte;	}	public function getId_lot() {		return $this->id_lot;	}	public function getNum_lot() {		return $this->num_lot;	}	public function getId_froid() {		return $this->id_froid;	}	public function getCode_froid() {		return $this->code_froid;	}	/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setId_emballage($valeur) {		$this->id_emballage = (int)$valeur;		Outils::setAttributs('id_emballage',$this);	}	public function setDate($valeur) {		$this->date = (string)$valeur;		Outils::setAttributs('date',$this);	}	public function setQte($valeur) {		$this->qte = (int)$valeur;		Outils::setAttributs('qte',$this);	}	public function setId_lot($valeur) {		$this->id_lot = (int)$valeur;		Outils::setAttributs('id_lot',$this);	}	public function setNum_lot($valeur) {		$this->num_lot = (string)$valeur;	}	public function setId_froid($valeur) {		$this->id_froid = (int)$valeur;		Outils::setAttributs('id_froid',$this);	}	public function setCode_froid($valeur) {		$this->code_froid = (string)$valeur;	}} // FIN classe