<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet EmballageFamille------------------------------------------------------*/class EmballageFamille {		protected	$id,				$nom,				$code,				$vues,				$actif,				$supprime,				$date_add,				$date_maj,				$non_consommes,				$emb_encours;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNom() {		return $this->nom;	}	public function getCode() {		return $this->code;	}	public function getActif() {		return $this->actif;	}	public function getSupprime() {		return $this->supprime;	}	public function getNon_consommes() {		return $this->non_consommes;	}	public function getDate_maj() {		return $this->date_maj;	}	public function getDate_add() {		return $this->date_add;	}	public function getVues() {		return $this->vues;	}	public function getEmb_encours() {		return $this->emb_encours;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setCode($valeur) {		$this->code = (string)$valeur;		Outils::setAttributs('code',$this);	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	public function setNon_consommes($valeur) {		$this->non_consommes = (int)$valeur;	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	public function setDate_add($valeur) {		$this->date_add = (string)$valeur;		Outils::setAttributs('date_add',$this);	}	public function setDate_maj($valeur) {		$this->date_maj = (string)$valeur;		Outils::setAttributs('date_maj',$this);	}	public function setVues($valeur) {		$this->vues = $valeur;	}	public function setEmb_encours($valeur) {		$this->emb_encours = $valeur;	}	/*##### MTHODES PROPRES A LA CLASSE #####*/	public function isActif() {		return (int)$this->actif == 1 && (int)$this->supprime == 0;	}	public function isSupprime() {		return (int)$this->supprime == 1;	}	public function getVuesIds() {		$liste = [];		if (!is_array($this->vues)) { return $liste; }		foreach ($this->vues as $vue) {			if (!$vue instanceof Vue) { continue; }			$liste[] = $vue->getId();		}		return $liste;	}} // FIN classe