<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 Cédric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet Incident------------------------------------------------------*/class Incident {	protected		$id,		$id_lot,		$negoce,		$type_incident,		$nom_type_incident,		$date,		$id_user,		$nom_user;	public       $attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId() {		return $this->id;	}	public function getId_lot() {		return $this->id_lot;	}	public function getType_incident() {		return $this->type_incident;	}	public function getNom_type_incident() {		return $this->nom_type_incident;	}	public function getDate() {		return $this->date;	}	public function getId_user() {		return $this->id_user;	}	public function getNom_user() {		return $this->nom_user;	}	public function getNegoce() {		return $this->negoce;	}	/* ----------------- SETTERS ----------------- */	public function setId($id) {		$this->id = (int)$id;	}	public function setId_lot($valeur) {		$this->id_lot = (int)$valeur;		Outils::setAttributs('id_lot',$this);	}	public function setType_incident($valeur) {		$this->type_incident = (int)$valeur;		Outils::setAttributs('type_incident',$this);	}	public function setNom_type_incident($valeur) {		$this->nom_type_incident = (string)$valeur;	}	public function setDate($valeur) {		$this->date= (string)$valeur;		Outils::setAttributs('date',$this);	}	public function setId_user($valeur) {		$this->id_user = (int)$valeur;		Outils::setAttributs('id_user',$this);	}	public function setNegoce($valeur) {		$this->negoce = (int)$valeur;		Outils::setAttributs('negoce',$this);	}	public function setNom_user($valeur) {		$this->nom_user = (string)$valeur;	}	/* ----------------- METHODES ----------------- */	public function getCodeIncident() {		// Lettre "i" pour Incident		// 2ème lettre du type (V pour eVisceration, S pour eSpece...) et l'ID en BDD sur 2 digits minimum		return strtoupper( 'i' . substr($this->getNom_type_incident(),1,1) . sprintf("%02d", $this->id));	}} // FIN classe