<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet IncidentType------------------------------------------------------*/class IncidentType {	protected		$id,		$nom,		$actif;	public       $attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId() {		return $this->id;	}	public function getNom() {		return $this->nom;	}	public function getActif() {		return $this->actif;	}	/* ----------------- SETTERS ----------------- */	public function setId($id) {		$this->id = (int)$id;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	/* ----------------- METHODES ----------------- */} // FIN classe