<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet Log------------------------------------------------------*/class Log {	protected		$id,		$log_type,		$log_datetime,		$log_texte,		$log_user_id,		$log_ip,		$nom_user;	public       $attributs = array();	public function __construct($donnees = [])	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId() {		return $this->id;	}	public function getLog_type() {		return $this->log_type;	}	public function getLog_datetime() {		return $this->log_datetime;	}	public function getLog_texte() {		return $this->log_texte;	}	public function getLog_user_id() {		return $this->log_user_id;	}	public function getLog_ip() {		return $this->log_ip;	}	public function getNom_user() {		return $this->nom_user;	}	/* ----------------- SETTERS ----------------- */	public function setId($id) {		$this->id = (int)$id;	}	public function setLog_type($valeur) {		$this->log_type = (string)$valeur;		Outils::setAttributs('log_type',$this);	}	public function setLog_datetime($valeur) {		$this->log_datetime = (string)$valeur;		Outils::setAttributs('log_datetime',$this);	}	public function setLog_texte($valeur) {		$log_texte = substr($valeur,0,254);		$this->log_texte = (string)$valeur;		Outils::setAttributs('log_texte',$this);	}	public function setLog_user_id($valeur) {		$this->log_user_id = (int)$valeur;		Outils::setAttributs('log_user_id',$this);	}	public function setLog_ip($valeur = '') {		if ($valeur == '') {			$valeur = $_SERVER['REMOTE_ADDR'];		}		$this->log_ip = (string)$valeur;		Outils::setAttributs('log_ip',$this);	}	public function setNom_user($valeur) {		$this->nom_user = (string)$valeur;	}} // FIN classe