<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet LogMail------------------------------------------------------*/class LogMail {	protected		$id,		$expediteur,		$destinataires,		$message,		$sujet,		$type,		$date_envoi;	public       $attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId() {		return $this->id;	}	public function getExpediteur() {		return $this->expediteur;	}	public function getDestinataires() {		return $this->destinataires;	}	public function getMessage() {		return $this->message;	}	public function getSujet() {		return $this->sujet;	}	public function getType() {		return $this->type;	}	public function getDate_envoi() {		return $this->date_envoi;	}	/* ----------------- SETTERS ----------------- */	public function setId($id) {		$this->id = (int)$id;	}	public function setExpediteur($valeur) {		$this->expediteur = (string)$valeur;		Outils::setAttributs('expediteur',$this);	}	public function setDestinataires($valeur) {		$this->destinataires = (string)$valeur;		Outils::setAttributs('destinataires',$this);	}	public function setMessage($valeur) {		$this->message = (string)$valeur;		Outils::setAttributs('message',$this);	}	public function setSujet($valeur) {		$this->sujet = (string)$valeur;		Outils::setAttributs('sujet',$this);	}	public function setType($valeur) {		$this->type = (int)$valeur;		Outils::setAttributs('type',$this);	}	public function setDate_envoi($valeur) {		$this->date_envoi = (string)$valeur;		Outils::setAttributs('date_envoi',$this);	}	/* ----------------- METHODES PROPRES A LA CLASSE ----------------- */	public function getDestinatairesArray() {		return explode(';', $this->destinataires);	}} // FIN classe