<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------LotRegroupement------------------------------------------------------*/class LotRegroupement {		protected	$id,				$numlot,				$statut,				$date_add,				$user_id,				$supprime;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNumlot() {		return $this->numlot;	}	public function getStatut() {		return $this->statut;	}	public function getDate_add() {		return $this->date_add;	}	public function getUser_id() {		return $this->user_id;	}	public function getSupprime() {		return $this->supprime;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNumlot($valeur) {		$this->numlot = (string)$valeur;		Outils::setAttributs('numlot',$this);	}	public function setStatut($valeur) {		$this->statut = (int)$valeur;		Outils::setAttributs('statut',$this);	}	public function setDate_add($valeur) {		$this->date_add = (string)$valeur;		Outils::setAttributs('date_add',$this);	}	public function setUser_id($valeur) {		$this->user_id = (int)$valeur;		Outils::setAttributs('user_id',$this);	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}} // FIN classe