<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------LotVue------------------------------------------------------*/class LotVue {	protected	$id,				$id_lot,				$id_vue,				$date_entree,				$vue;			// Objet Vue		public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getId_lot() {		return $this->id_lot;	}	public function getId_vue() {		return $this->id_vue;	}	public function getDate_entree() {		return $this->date_entree;	}	public function getVue() {		return $this->vue;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setId_lot($valeur) {		$this->id_lot = (int)$valeur;		Outils::setAttributs('id_lot',$this);	}	public function setId_vue($valeur) {		$this->id_vue = (int)$valeur;		Outils::setAttributs('id_vue',$this);	}	public function setDate_entree($valeur) {		$this->date_entree = (string)$valeur;		Outils::setAttributs('date_entree',$this);	}	public function setVue($valeur) {		$this->vue = $valeur;	}} // FIN classe