<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 Cédric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet ProduitEspece------------------------------------------------------*/class ProduitEspece {		protected	$id,				$nom,				$couleur,				$abats,				$actif,				$supprime,				$date_add,				$date_maj;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNom() {		return $this->nom;	}	public function getCouleur() {		return $this->couleur;	}	public function getAbats() {		return $this->abats;	}	public function getActif() {		return $this->actif;	}	public function getSupprime() {		return $this->supprime;	}	public function getDate_maj() {		return $this->date_maj;	}	public function getDate_add() {		return $this->date_add;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setCouleur($valeur) {		// On force le # pour l'hexa si on a oublié...		if (substr($valeur,0,1) != '#') {			$valeur = '#'.$valeur;		}		$this->couleur = (string)$valeur;		Outils::setAttributs('couleur',$this);	}	public function setCode($valeur) {		$this->code = (string)$valeur;		Outils::setAttributs('code',$this);	}	public function setAbats($valeur) {		$this->abats = (int)$valeur;	}	public function setActif($valeur) {		$this->actif = (int)$valeur;		Outils::setAttributs('actif',$this);	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	public function setDate_add($valeur) {		$this->date_add = (string)$valeur;		Outils::setAttributs('date_add',$this);	}	public function setDate_maj($valeur) {		$this->date_maj = (string)$valeur;		Outils::setAttributs('date_maj',$this);	}	/*##### MTHODES PROPRES A LA CLASSE #####*/	public function isActif() {		return (int)$this->actif == 1 && (int)$this->supprime == 0;	}	public function isSupprime() {		return (int)$this->supprime == 1;	}} // FIN classe