<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2020 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet ProduitStats------------------------------------------------------*/class ProduitStats {		protected	$id,				$code,				$nom;	public		$attributs = array();		public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNom() {		return $this->nom;	}	public function getCode() {		return $this->code;	}	/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;	}	public function setCode($valeur) {		$this->code = (string)$valeur;	}} // FIN classe