<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 CÃ©dric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet Profil------------------------------------------------------*/class Profil {	protected		$id,		$nom,		$fa,		$is_nett,		$is_resp,		$is_admin,		$is_gescom,		$is_dev,		$niveau;	public       $attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId() {		return $this->id;	}	public function getNom() {		return $this->nom;	}	public function getFa() {		return $this->fa;	}	public function getIs_nett() {		return $this->is_nett;	}	public function getIs_resp() {		return $this->is_resp;	}	public function getIs_admin() {		return $this->is_admin;	}	public function getIs_gescom() {		return $this->is_gescom;	}	public function getIs_dev() {		return $this->is_dev;	}	public function getNiveau() {		return $this->niveau;	}	/* ----------------- SETTERS ----------------- */	public function setId($id) {		$this->id = (int)$id;	}	public function setNom($nom) {		$this->nom = (string)$nom;		Outils::setAttributs('nom',$this);	}	public function setFa($fa) {		$this->fa = (string)$fa;		Outils::setAttributs('fa',$this);	}	public function setIs_nett($is_nett) {		$this->is_nett = (int)$is_nett;		Outils::setAttributs('is_nett',$this);	}	public function setIs_resp($is_resp) {		$this->is_resp = (int)$is_resp;		Outils::setAttributs('is_resp',$this);	}	public function setIs_gescom($is) {		$this->is_gescom = (int)$is;		Outils::setAttributs('is_gescom',$this);	}	public function setIs_admin($is_admin) {		$this->is_admin = (int)$is_admin;		Outils::setAttributs('is_admin',$this);	}	public function setIs_dev($is_dev) {		$this->is_dev = (int)$is_dev;		Outils::setAttributs('is_dev',$this);	}	public function setNiveau($niveau) {		$this->niveau = (int)$niveau;		Outils::setAttributs('niveau',$this);	}} // FIN classe