<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 Cédric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet TiersFamille------------------------------------------------------*/class TiersFamille {		protected	$id,				$nom,				$supprime,				$nb_tiers;	 // Nb de tiers associés (hors supprimés)	public		$attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}		public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}				$this->attributs = array();	}		/*##### GETTERS #####*/	public function getId() {		return $this->id;	}		public function getNom() {		return $this->nom;	}	public function getSupprime() {		return $this->supprime;	}	public function isSupprimee() {		return intval($this->supprime) == 1;	}	public function getNb_tiers() {		return $this->nb_tiers;	}				/*##### SETTERS #####*/	public function setId($valeur) {		$this->id = (int)$valeur;	}	public function setNom($valeur) {		$this->nom = (string)$valeur;		Outils::setAttributs('nom',$this);	}	public function setSupprime($valeur) {		$this->supprime = (int)$valeur;		Outils::setAttributs('supprime',$this);	}	public function setNb_tiers($valeur) {		$this->nb_tiers = (int)$valeur;	}} // FIN classe