<?php/*   _|_|_|  _|_|_|      _|_| _|        _|    _|  _|    _|  CBO FrameWork _|        _|_|_|    _|    _|  (c) 2018 Cédric Bouillon _|        _|    _|  _|    _|   _|_|_|  _|_|_|      _|_|--------------------------------------------------------Objet UserGénéré par CBO FrameWork le 13/11/2018 à 16:51:00------------------------------------------------------*/class User {	protected    $id,		$nom,		$prenom,		$email,		$profil_id,		$code,		$admin_hash,		$alertes,		$actif,		$supprime,		$date_creation,		$date_modif,		$is_resp,		$is_admin,		$is_gescom,		$is_dev;	public       $attributs = array();	public function __construct(array $donnees)	{		$this->hydrate($donnees);	}	public function hydrate(array $donnees)	{		foreach ($donnees as $key => $value) {			$method = 'set'.ucfirst(strtolower($key));			if (method_exists($this,$method)) {				$this->$method($value);			}		}		$this->attributs = array();	}	/* ----------------- GETTERS ----------------- */	public function getId() {		return $this->id;	}	public function getNom() {		return $this->nom;	}	public function getPrenom() {		return $this->prenom;	}	public function getEmail() {		return $this->email;	}	public function getProfil_id() {		return $this->profil_id;	}	public function getCode() {		return $this->code;	}	public function getAdmin_hash() {		return $this->admin_hash;	}	public function getAlertes() {		return $this->alertes;	}	public function getActif() {		return $this->actif;	}	public function getSupprime() {		return $this->supprime;	}	public function getDate_creation() {		return $this->date_creation;	}	public function getDate_modif() {		return $this->date_modif;	}	public function isActif() {		return intval($this->actif) == 1 && intval($this->supprime) == 0;	}	public function isResp() {		return (int)$this->is_resp == 1;	}	public function isAdmin() {		return (int)$this->is_admin == 1;	}	public function isGescom() {		return (int)$this->is_gescom == 1;	}	public function hasAlertes() {		return (int)$this->alertes == 1;	}	public function isDev() {		return (int)$this->is_dev == 1;	}	/* ----------------- SETTERS ----------------- */	public function setId($id) {		$this->id = (int)$id;	}	public function setNom($nom) {		$this->nom = (string)$nom;		Outils::setAttributs('nom',$this);	}	public function setPrenom($prenom) {		$this->prenom = (string)$prenom;		Outils::setAttributs('prenom',$this);	}	public function setEmail($email) {		$this->email = (string)$email;		Outils::setAttributs('email',$this);	}	public function setProfil_id($profil_id) {		$this->profil_id = (int)$profil_id;		Outils::setAttributs('profil_id',$this);	}	public function setCode($code) {		$this->code = (string)$code;		Outils::setAttributs('code',$this);	}	public function setAdmin_hash($hash) {		$this->admin_hash = (string)$hash;		Outils::setAttributs('admin_hash',$this);	}	public function setAlertes($alertes) {		$this->alertes = (int)$alertes;		Outils::setAttributs('alertes',$this);	}	public function setActif($actif) {		$this->actif = (int)$actif;		Outils::setAttributs('actif',$this);	}	public function setSupprime($supprime) {		$this->supprime = (int)$supprime;		Outils::setAttributs('supprime',$this);	}	public function setDate_creation($date_creation) {		$this->date_creation = (string)$date_creation;		Outils::setAttributs('date_creation',$this);	}	public function setDate_modif($date_modif) {		$this->date_modif = (string)$date_modif;		Outils::setAttributs('date_modif',$this);	}	public function setIs_resp($is_resp) {		$this->is_resp = (int)$is_resp;	}	public function setIs_admin($is_admin) {		$this->is_admin = (int)$is_admin;	}	public function setIs_gescom($is) {		$this->is_gescom = (int)$is;	}	public function setIs_dev($is_dev) {		$this->is_dev = (int)$is_dev;	}	/* ----------------- METHODES SPECIFIQUES A LA CLASSE ----------------- */	public function getNomComplet() {		return ucwords(strtolower($this->prenom)) . ' ' .strtoupper($this->nom);	}	public function getTrigramme() {		return strtoupper(substr($this->prenom,0,1)).strtoupper(substr($this->nom,0,2));	}} // FIN classe