r;		Outils::setAttributs('nom',$this);	}	public function setUrl($valeur) {		$this->url = (string)$valeur;		Outils::setAttributs('url',$this);	}	public function setOrdre($valeur) {		$this->ordre = (int)$valeur;		Outils::setAttributs('ordre',$this);	}	public function setEmballage($valeur) {		$this->emballage = (int)$valeur;		Outils::setAttributs('emballage',$this);	}} // FIN classe