div class="col-12">                <div class="input-group mb-3">                    <div class="input-group-prepend"><span class="input-group-text">Palette</span></div>                    <input type="text" class="form-control" name="palette" id="filtre_palette" placeholder="N° palette" value="">                </div>            </div>            <div class="col-12">                <div class="input-group mb-3">                    <div class="input-group-prepend"><span class="input-group-text">Date</span></div>                    <?php $aujourdhui = date('Y-m-d'); ?>                    <select class="selectpicker form-control show-tick" name="date" id="filtre_date" data-aujourdhui="<?php echo $aujourdhui; ?>">                        <?php                        for ($i = 0 ; $i < 6; $i++) { ?>                            <option data-subtext="<?php                                    echo $i > 0 ? date('d/m/Y', strtotime( $aujourdhui.' - '.$i.' DAY')) : ''; ?>" value="<?php                                    echo date('Y-m-d', strtotime($aujourdhui.' - '.$i.' DAY')); ?>" <?php                                    echo $i == 0 ? 'selected' : ''; ?>><?php                                    echo $i > 0 ? "J -" . $i : "Aujourd'hui"; ?></option>                        <?php } ?>                        <option value="">Toujours</option>                    </select>                </div>            </div>        </div>        <button type="button" class="btn btn-info btn-lg form-control padding-20-40" id="btnGoRecherche">            <i class="fa fa-search mr-1"></i>            Rechercher&hellip;        </button>        <p id="resultTotaux"></p>        <button type="button" class="btn btn-secondary btn-lg form-control padding-20-40 mt-4" id="btnRazRecherche">            <i class="fa fa-undo-alt mr-1"></i>            Réinitialiser        </button>        <input type="hidden" id="filtre_actif_clavier" value="filtre_lot"/>        <input type=